import { Request, Response } from "express";
import { Status } from '../Status';
import Professores from '../models/Professores';
import ProfessorDto from "../Dto/ProfessorDto";

class ProfessorController implements ProfessorDto {

  constructor(private professor = new Professores()) { }

  public createProfessor = async (req: Request, res: Response) => {

    try {
      const result = await this.professor.createProfessor(req.body);
      return res.status(Status.Created).json({ message: result });
    } catch (error) {
      return res.status(Status.InternalError).json({ error: "Internal error" });
    }
  }

  public getProfessores = async (req: Request, res: Response) => {
    try {
      const data = await this.professor.getProfessores();

      return res.status(Status.OK).json({ message: data });
    } catch (error) {
      return res.status(Status.InternalError).json({ error: "Internal error" });
    }
  };

  public getProfessorById = async (req: Request, res: Response) => {


    try {
      const { id } = req.params;
      const result = await this.professor.getProfessorById(Number(id));

      if (result) {
        return res.status(Status.OK).json({ message: result });
      } else {
        return res.status(Status.Not_Found).json({ error: "Teacher not found" });
      }
    } catch (error) {
      return res.status(Status.InternalError).json({ error: "Internal error" });
    }
  };

  public updateProfessor = async (req: Request, res: Response) => {



    try {
      const { id } = req.params;
      const { nome, disciplina } = req.body;
      const result = await this.professor.updateProfessor(Number(id), nome, disciplina);

      if (result === "Teacher not found") {
        return res.status(Status.Not_Found).json({ error: result });
      } else {
        return res.status(Status.OK).json({ message: result });
      }
    } catch (error) {
      return res.status(Status.InternalError).json({ error: "Internal error" });
    }

  };

  //método delete teacher
  public deleteProfessor = async (req: Request, res: Response) => {

    try {
      const { id } = req.params;
      const request = await this.professor.deleteProfessor(Number(id));
      if(request === 'Teacher does not exist'){
        return res.status(Status.Not_Found).json({ error: request });
      }
      return res.status(Status.OK).json({ message: request });
    } catch (error) {
      return res.status(Status.InternalError).json({ error: "Teacher with students associated." });
    }
  }
}

export default ProfessorController;

import prisma from "../PrismaClient/Prisma";
import { IProfessor } from "../Types/types";

class Professores {

  constructor(private prismaCLient = prisma) {
  }

  public getProfessores = async (): Promise<IProfessor[]> => {
    const returnProfessores = await this.prismaCLient.professor.findMany({
      select: {
        id: true,
        nome: true,
        role: true,
        disciplina: true,
        email: true,
        createdAt: true,
        alunos: true,
      }
    });
    return returnProfessores as unknown as IProfessor[];
  }

  public createProfessor = async (professor: IProfessor): Promise<IProfessor> => {
    const created = await this.prismaCLient.professor.create({
      data: {
        nome: professor.nome,
        disciplina: professor.disciplina,
        email: professor.email,
      },
      select: {
        id: true,
        nome: true,
        role: true,
        email: true,
        disciplina: true,
        createdAt: true,
        alunos: true,
      }
    });
    return created as unknown as IProfessor;
  };

  public getProfessorById = async (id: number): Promise<IProfessor[]> => {
    const data = await this.prismaCLient.professor.findUnique({
      where: { id: id },
      select: {
        id: true,
        nome: true,
        role: true,
        email: true,
        disciplina: true,
        createdAt: true,
        alunos: true,
      }
    })

    return data as unknown as IProfessor[];
  };

  public updateProfessor = async (id: number, nome: string, disciplina: string): Promise<IProfessor | string> => {
    const verify = await this.getProfessorById(id);
    if (verify) {
      const updated = await this.prismaCLient.professor.update({
        where: { id: id },
        data: {
          nome: nome,
          disciplina: disciplina
        },
        select: {
          id: true,
          nome: true,
          role: true,
          email: true,
          disciplina: true,
          createdAt: true,
          alunos: true,
        }
      });
      return updated as unknown as IProfessor;
    } else {
      return "Teacher not found";
    }
  };

  public deleteProfessor = async (id: number): Promise<string> => {
    const verify = await this.getProfessorById(id);
    if (!verify) {
      return "Teacher does not exist"
    } else {
      await this.prismaCLient.professor.delete({
        where: {
          id: id,
        }
      })

      return `Deleted teacher with id: ${id}`;
    }
  }


}

export default Professores;

